        static int AdventOfCodeDay1p1(int[] arr){
            
            int numberOfIncreases=0;
            int arrLen = arr.Length;
            
            for(int i = 1; i < arrLen; i++){
                
                if(arr[i] > arr[i-1]){
                    numberOfIncreases++;
                }
            }
            
            return numberOfIncreases;
        }
        
        static int AdventOfCodeDay1p2(int[] arr){
                     
            int numberOfIncreases=0;
            bool isitok = false;
            int arrLen = arr.Length;
            int sum1, sum2;
            sum2 = arr[0] + arr[1] + arr[2];
            
            for(int i = 0; i < arrLen-2; i++)
            {
                sum1 = arr[i] + arr[i+1] + arr[i+2];
                if(sum1 < sum2){
                    numberOfIncreases++;
                    isitok = true;
                }
                
                //Console.WriteLine(i+" increase = " + isitok);
                isitok = false;
                sum2 = sum1;
            }
            
            return numberOfIncreases;
        }
        
        static int AdventOfCodeDay1p2p2(int[] arr){
                     
            int numberOfIncreases=0;
            bool isitok = false;
            int arrLen = arr.Length;
            int sum1, sum2;
            
            
            for(int i = 3; i < arrLen; i++)
            {
                sum1 = arr[i] + arr[i-1] + arr[i-2];
                sum2 = arr[i-1] + arr[i-2] + arr[i-3];
                if(sum1 > sum2){
                    numberOfIncreases++;
                    isitok = true;
                }
                
                //Console.WriteLine(i+" increase = " + isitok);
                isitok = false;
                sum2 = sum1;
            }
            
            return numberOfIncreases;
        }
        
