            {
                
                string[] split = words.Split(' ');

                int value = Convert.ToInt32(split[1]);
                switch (split[0])
                {
                    case "down":
                        aim += value;
                        break;
                    
                    case "up":
                        aim -= value;
                        break;
                    
                    case "forward":
                        horrisontal += value;
                        deapth +=  value * aim ;
                        break;
                    
                }
            }
            return horrisontal * deapth;
        }

